import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const DEFAULT_IMAGE = 'https://via.placeholder.com/300x200?text=Конфетти';

const ProductCard = ({ product }) => {
  const { addToCart } = useContext(CartContext);

  const handleAddToCart = () => {
    addToCart(product);
  };

  return (
    <div className="card h-full flex flex-col">
      <div className="relative pb-[66.666%] overflow-hidden">
        <img
          src={product.imageUrl || DEFAULT_IMAGE}
          alt={product.name}
          className="absolute h-full w-full object-cover transition-transform duration-500 hover:scale-105"
        />
      </div>
      <div className="p-4 flex-grow flex flex-col">
        <div className="flex-grow">
          <Link to={`/products/${product.id}`}>
            <h3 className="text-lg font-semibold text-gray-800 hover:text-pink-500 transition-colors mb-2">
              {product.name}
            </h3>
          </Link>
          {product.category && (
            <div className="mb-2">
              <span className="inline-block bg-pink-100 text-pink-600 text-xs px-2 py-1 rounded-full">
                {product.category.name}
              </span>
            </div>
          )}
          <p className="text-gray-600 text-sm mb-4 line-clamp-2">
            {product.description || 'Нет описания'}
          </p>
        </div>
        <div className="mt-auto flex items-center justify-between">
          <span className="text-xl font-bold text-gray-800">{product.price.toFixed(2)} ₽</span>
          <button
            onClick={handleAddToCart}
            className="btn-primary rounded-full w-10 h-10 flex items-center justify-center"
            aria-label="Добавить в корзину"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-5 w-5"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 6v6m0 0v6m0-6h6m-6 0H6"
              />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
