import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import ProductCard from '../components/ProductCard';

const HomePage = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Получение категорий
        const { data: categoriesData } = await axios.get('/categories');
        setCategories(categoriesData);

        // Получение продуктов для главной страницы (ограничиваем 4 продуктами)
        const { data: productsData } = await axios.get('/products');
        setFeaturedProducts(productsData.slice(0, 4));

        setLoading(false);
      } catch (err) {
        console.error('Ошибка загрузки данных:', err);
        setError('Ошибка загрузки данных. Пожалуйста, попробуйте позже.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {/* Баннер */}
      <section className="bg-gradient-to-r from-pink-100 to-pink-200 rounded-xl overflow-hidden shadow-lg mb-12">
        <div className="container mx-auto px-4 py-12 md:py-20">
          <div className="text-center max-w-3xl mx-auto">
            <h1 className="text-3xl md:text-4xl lg:text-5xl font-bold text-gray-800 mb-4">
              Добро пожаловать в "Конфетти"
            </h1>
            <p className="text-lg md:text-xl text-gray-700 mb-8">
              Магазин изысканных кондитерских изделий для любого случая
            </p>
            <Link
              to="/products"
              className="btn btn-primary text-lg px-8 py-3"
            >
              Перейти в каталог
            </Link>
          </div>
        </div>
      </section>

      {/* Категории */}
      <section className="mb-12">
        <div className="container mx-auto px-4">
          <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-6 text-center">
            Категории
          </h2>

          {loading ? (
            <div className="text-center py-8">Загрузка категорий...</div>
          ) : error ? (
            <div className="text-center text-red-500 py-8">{error}</div>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {categories.map(category => (
                <Link
                  key={category.id}
                  to={`/products?category=${category.id}`}
                  className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow"
                >
                  <div className="p-6 text-center">
                    <h3 className="text-xl font-semibold text-gray-800 mb-2">
                      {category.name}
                    </h3>
                    <span className="inline-block bg-pink-100 text-pink-600 px-3 py-1 rounded-full text-sm">
                      Смотреть товары
                    </span>
                  </div>
                </Link>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Популярные товары */}
      <section className="mb-12">
        <div className="container mx-auto px-4">
          <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-6 text-center">
            Популярные товары
          </h2>

          {loading ? (
            <div className="text-center py-8">Загрузка товаров...</div>
          ) : error ? (
            <div className="text-center text-red-500 py-8">{error}</div>
          ) : (
            <>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                {featuredProducts.map(product => (
                  <ProductCard key={product.id} product={product} />
                ))}
              </div>

              <div className="text-center mt-8">
                <Link
                  to="/products"
                  className="inline-block btn btn-primary"
                >
                  Посмотреть все товары
                </Link>
              </div>
            </>
          )}
        </div>
      </section>

      {/* Преимущества */}
      <section className="mb-12 bg-gray-50 py-12 rounded-xl">
        <div className="container mx-auto px-4">
          <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-8 text-center">
